import Header from './Header'
import React, { useState,useEffect} from "react";
import { useHistory } from "react-router-dom";

function Login(){

    useEffect(()=>{
        if(localStorage.getItem('user-info'))
        {
            history.push('/add')
        }
    })
    return(
        <div>
            <Header />
            <h1> Login Page</h1>
        </div>
    )
}
export default Login;
